import React,{Component} from "react";
import TeamMember from './TeamMember';

class Team extends Component {
    constructor(){
        super();
        this.state={
            // object
            info : membersInfo
        }
    }
    mapping(x){
        const members = this.state.info.map(member => (
            <TeamMember
                key={member.id} // Unique key prop for each child
                name={member.name}
                img={member.img}
                position={member.position}
                phone={member.phone}
                email={member.email || 'N/A'} // Fallback for missing email
                website={member.website}
            />
        ));
        return members;
    }
    render(){

        return (
            <div className="row">
                {this.mapping(this.state.info)}
            </div>
        )
    }


}
export default Team;
